    private void btnequalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnequalActionPerformed
        
        if(s.length()>0){   
        String w[]=s.split("\\+|-|\\*|/|%");
        String[] x = s.split("(?<=[-+*/])|(?=[-+*/])");

        double result=0;
        try{
            for (String w1 : w) {
                if (s.contains("*")) {
                    if (result ==0) {
                        result = (double) Double.parseDouble(w1);
                    } else {
                        result *= (double) Double.parseDouble(w1);
                    }
                    result=Math.round(result*100)/100.0;
                    Display2.setText(Double.toString(result));
                }if (s.contains("/")) {
                    if (result ==0) {
                        result = (double) Double.parseDouble(w1);
                    } else if(Double.parseDouble(w1)!=0){
                        //if(Double.parseDouble(w[i+1])!=0){
                        result /= (double) Double.parseDouble(w1);
                        result=Math.round(result*100)/100.0;
                        //Display.setText(double.toString(a*b));
                        Display2.setText(Double.toString(result));
                    }else
                    {
                       Display.setText("Math Error");
                       break;
                    }
                }if (s.contains("+")) {
                    result += (double) Double.parseDouble(w1);
                    result=Math.round(result*100)/100.0;
                    Display2.setText(Double.toString(result));
                }if (s.contains("-")) {
                    if (result ==0) {
                        result = (double) Double.parseDouble(w1);
                    } else {
                        result -= (double) Double.parseDouble(w1);
                    }
                    result=Math.round(result*100)/100.0;
                    Display2.setText(Double.toString(result));
                } 
            }
            }catch(NumberFormatException e){
                Display.setText("Syntax Error");
                s=new String();
                Display2.setText(s);
            }
        
        if(result != 0){
            value=result;
        }
        }
        s=new String();
    }//GEN-LAST:event_btnequalActionPerformed

