    private void btnprsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnprsActionPerformed
        if(s.length()>0){
        s=s+"%";
        Display.setText(s);
        //double b=0;
        String w[]=s.split("\\+|-|\\*|/|%");
        double result=0;
        double p=100;
        int flage=0;
        if(w.length < 2){
            result=Double.parseDouble(w[0]);
            Display2.setText(Double.toString(result));
        }else if(w.length > 2){
            Display.setText("Syntax Error");
        }else if(w.length > 1){
            double a=Double.parseDouble(w[0]);
            double b=Double.parseDouble(w[1]);
        if(s.contains("+"))
        {
            
            if(Double.parseDouble(w[1])!=0){
                
                result=(double)((a+b)*p)/b;
                result=Math.round(result*100)/100.0;
                Display2.setText(Double.toString(result));
          }else
            {
                Display.setText("Math Error");
            }
        }else if(s.contains("-")){
            
            if(b!=0){
                result=(double)((a-b)*p)/b;
                result=Math.round(result*100)/100.0;
                Display2.setText(Double.toString(result));
            }else
            {
                Display.setText("Math Error");
            }
        }else if(s.contains("*")){
            
            result=(double)a*(b/p);
            result=Math.round(result*100)/100.0;
            Display2.setText(Double.toString(result));
            
        }else if(s.contains("/")){
            
            if(b!=0){
                result=(double)a*p/b;
                result=Math.round(result*100)/100.0;
                Display2.setText(Double.toString(result));
            }else
            {
                Display.setText("Math Error");
            } 
        }
        
        }
        if(result != 0){
            value=result;
        }
        
        }
    }//GEN-LAST:event_btnprsActionPerformed

