    private void btnTanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTanActionPerformed
        double result=0;
        if(s.length()>0 || value !=0){
        if(value == 0){
           result=Double.parseDouble(s);
           
           Display.setText("tan"+"("+s+")");
           
           double result1=Math.sin(Math.toRadians(result));
           double result2=Math.cos(Math.toRadians(result));
           if(result2 != 0){
               result=result1/result2;
               result=Math.round(result*100)/100.0;
               Display2.setText(Double.toString(result));
           }else if(result2 ==0){
               Display.setText("Math Error");
           }
                    
        }else{
           s=Double.toString(value);
           Display.setText("tan"+"("+s+")");
        
           double result1=Math.sin(Math.toRadians(value));
           double result2=Math.cos(Math.toRadians(value));
           if(result2 !=0){
               result=result1/result2;
               result=Math.round(result*100)/100.0;
               Display2.setText(Double.toString(result));
           }else if(result2 ==0){
               Display.setText("Math Error");
           }
        }
        }
        if(result != 0){
            value=result;
        }
    }//GEN-LAST:event_btnTanActionPerformed

